import React, { useState } from "react";
import Header from "../components/Header";
import Heading from "../components/Heading";
import Grid from "@mui/material/Grid";
import Images from "../components/Images";
import InputBox from "../components/InputBox";
import PButton from "../components/PButton";
import { styled } from "@mui/material/styles";
import Button, { ButtonProps } from "@mui/material/Button";
import AuthenticationLink from "../components/AuthenticationLink";
import { AiFillEye, AiFillEyeInvisible } from "react-icons/ai";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";

const commonButton = styled(Button)({
  width: "100%", 
  boxShadow: "none",
  textTransform: "none",
  fontSize: "20.64px",
  padding: "19px 12px",
  backgroundColor: "#5F35F5",
  marginTop: "56px",
  fontFamily: ["Nunito", "sans-serif"],
  "&:hover": {
    backgroundColor: "#000",
  },
});

const Login = () => {

  let auth = getAuth()

  let [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  let [error, setError] = useState({
    email: "",
    password: "",
  });


  let handleClick =()=>{
    signInWithEmailAndPassword(auth, formData.email, formData.password)
  .then((userCredential) => {
    console.log("kaj hoiche")
  })
  .catch((error) => {
    const errorCode = error.code;
    const errorMessage = error.message;
  });

  }

  let handleForm = (e) => {
    let { name, value } = e.target;

    if (name === "password") {
      let capi = /[A-Z]/;
      let lower = /[a-z]/;
      let num = /[0-9]/;
      if (!capi.test(value)) {
        setError({ ...error, password: "One Capital Letter Rewuired" });
        return;
      }
      if (!lower.test(value)) {
        setError({ ...error, password: "One Small Letter Rewuired" });
        return;
      }
      if (!num.test(value)) {
        setError({ ...error, password: "One Number Rewuired" });
        return;
      }
      if (value.length < 6) {
        setError({ ...error, password: "Password length atlest 6" });
        return;
      }
    }

    setFormData({ ...formData, [name]: value });
    setError({ ...error, [name]: "" });
  };


  let [show, setShow] = useState(false);

  return (
    <>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <div className="regrightside">
            <div>
              <Header>
                <Heading
                  title="Login to your account!"
                  className="heading"
                  as="h2"
                />
              </Header>
              <div className="googlelogin">
                <Images imgsrc="assets/googlelogin.png" />
              </div>
              <div className="inputboxcontainer">
                <InputBox
                textChange={handleForm}
                  className="reginput"
                  label="Email Address"
                  variant="standard"
                  name="email"
                />
                <InputBox
                    className="reginput"
                    label="Password"
                    variant="outlined"
                    textChange={handleForm}
                    type={show ? "text" : "password"}
                    name="password"
                  />

{show ? (
                    <AiFillEye
                      onClick={() => setShow(false)}
                      className="eyeicon"
                    />
                  ) : (
                    <AiFillEyeInvisible
                      onClick={() => setShow(true)}
                      className="eyeicon"
                    />
                  )}
                </div>

                {error.password && (
                  <Alert className="error" variant="filled" severity="error">
                    {error.password}
                  </Alert>
                )}

                <PButton click ={handleClick} bname={commonButton} title="Login to Continue" />

                <AuthenticationLink
                  className="reglink"
                  title="Donâ€™t have an account ?"
                  href="/"
                  hreftitle="Sign Up"
                />
              </div>
            </div>
        </Grid>
        <Grid item xs={6}>
          <Images className="regimg" imgsrc="assets/regimgTwo.png" />
        </Grid>
      </Grid>
    </>
  );
};

export default Login;
